SELECT DISTINCTROW Categories.CategoryName, Categories.Description, Categories.Picture, Products.ProductID, Products.ProductName, Products.QuantityPerUnit, Products.UnitPrice
        FROM Categories INNER JOIN Products ON Categories.CategoryID = Products.CategoryID
        WHERE (((Products.Discontinued)=No));

PARAMETERS [Beginning Date] DateTime 1/1/2008, [Ending Date] DateTime 1/1/2015, [Sales Goal] Currency 100000;
         SELECT DISTINCTROW Employees.Country, Employees.LastName, Employees.FirstName, Orders.ShippedDate, Orders.OrderID, [Order Subtotals].Subtotal AS SaleAmount
         FROM Employees INNER JOIN (Orders INNER JOIN [Order Subtotals] ON Orders.OrderID = [Order Subtotals].OrderID) ON Employees.EmployeeID = Orders.EmployeeID
         WHERE (((Orders.ShippedDate) Between [Beginning Date] And [Ending Date]));

PARAMETERS [Critical Stock Level] Short 10;
SELECT DISTINCTROW Products.ProductName, [Order Details].UnitPrice, Sum([Order Details].Quantity) AS SumOfQuantity, Products.ReorderLevel, Products.UnitsInStock, [Order Details].Discount, Sum(CCur([Order Details].[UnitPrice]*[Quantity]*(1-[Discount])/100)*100) AS ExtendedPrice
FROM Products INNER JOIN [Order Details] ON Products.ProductID = [Order Details].ProductID
GROUP BY Products.ProductName, [Order Details].UnitPrice, Products.ReorderLevel, Products.UnitsInStock, [Order Details].Discount
ORDER BY Sum(CCur([Order Details].[UnitPrice]*[Quantity]*(1-[Discount])/100)*100) DESC;

PARAMETERS [Beginning Date] DateTime 1/1/2008, [Ending Date] DateTime 1/1/2015, [Show Details] Boolean False;
        SELECT DISTINCTROW Orders.ShippedDate, Orders.OrderID, [Order Subtotals].Subtotal, Format([ShippedDate],"yyyy") AS [TheYear]
        FROM Orders INNER JOIN [Order Subtotals] ON Orders.OrderID = [Order Subtotals].OrderID
        WHERE ((Not IsNull(Orders.ShippedDate) And (Orders.ShippedDate) Between [Beginning Date] And [Ending Date]));

PARAMETERS [Beginning Date] DateTime 1/1/2008, [Ending Date] DateTime 1/1/2015, [Show Details] Boolean False;
        SELECT DISTINCTROW Orders.ShippedDate, Orders.OrderID, [Order Subtotals].Subtotal, Format([ShippedDate],"yyyy") AS [TheYear]
        FROM Orders INNER JOIN [Order Subtotals] ON Orders.OrderID = [Order Subtotals].OrderID
        WHERE ((Not IsNull(Orders.ShippedDate) And (Orders.ShippedDate) Between [Beginning Date] And [Ending Date]));

SELECT DISTINCTROW Orders.ShippedDate, Orders.OrderID, [Order Subtotals].Subtotal, DatePart("q",Orders.ShippedDate) As ShippedQuarter
        FROM Orders INNER JOIN [Order Subtotals] ON Orders.OrderID = [Order Subtotals].OrderID
        WHERE (((Orders.ShippedDate) Is Not Null));
