        #region ** Templated icons
        // C1IconTemplate class is a factory which returns the most common predefined icons.
        // To get predefined icon, use one of C1IconTemplate static properties to get specific icon template 
        // and then call C1IconTemplate.CreateTemplate method. For example:
        // 
        //   C1Icon icon = C1IconTemplate.ChevronDown.CreateIcon();

        List<C1Icon> _templatedIcons = new List<C1Icon>();

        private void InitializeTemplatedIcons()
        {
            // Get all predefined icon templates
            var defaultIconTemplates = typeof(C1IconTemplate)
              .GetProperties(System.Reflection.BindingFlags.Public | System.Reflection.BindingFlags.Static)
              .Where(p => p.PropertyType == typeof(C1IconTemplate))
              .ToDictionary(p => p.Name,
                p => (C1IconTemplate)p.GetValue(null, null));

            // create icons from templates and add them to the _templatedIcons list
            foreach (var template in defaultIconTemplates.Values)
            {
                var icon = template.CreateIcon();
                _templatedIcons.Add(icon);
            }

            // show created icons in the ugridTemplatedIcons control
            ugridTemplatedIcons.Icons = _templatedIcons;
            ugridTemplatedIcons.ForeColor = this.ForeColor;
        }
        #endregion

