        #region ** Bitmap Icons
        // C1BitmapIcon can be used to show bitmaps with scaling and/or recoloring to flat color.
        List<C1Icon> _bitmapIcons = new List<C1Icon>();

        private void InitializeBitmapIcons()
        {
            // load all images embedded into application resources
            var imageDictionary = ResourceLoader.GetImages(typeof(Form1).Assembly);

            // create C1BitmapIcon instances for each image
            foreach(var entry in imageDictionary.Values)
            {
                var icon = new C1BitmapIcon();
                icon.Source = entry as Image;
                icon.Size = new Size(32, 32);
                _bitmapIcons.Add(icon);
            }

            ugridBitmapIcons.ForeColor = this.ForeColor;

            // show created icons in the ugridBitmapIcons control
            ugridBitmapIcons.Icons = _bitmapIcons;
        }
