        #region ** Object Model
        /// <summary>
        /// Gets or sets Color to draw icons.
        /// </summary>
        /// <remarks>If this property is set to Empty or Transparent color, icons will inherit Foreground color from the parent container.</remarks>
        Color Color
        {
            get
            {
                return _color;
            }
            set
            {
                if ( _color != value)
                {
                    _color = value;
                    foreach (C1Icon icon in _templatedIcons)
                    {
                        icon.Color = value;
                    }
                    foreach (C1Icon icon in _vectorIcons)
                    {
                        icon.Color = value;
                    }
                    foreach (C1Icon icon in _fontIcons)
                    {
                        icon.Color = value;
                    }
                    foreach (C1Icon icon in _bitmapIcons)
                    {
                        icon.Color = value;
                    }
                    foreach (C1Icon icon in _compositeIcons)
                    {
                        icon.Color = value;
                    }
                }

            }
        }

        /// <summary>
        /// Gets or sets icon stroke color.
        /// </summary>
        /// <remarks>This property only afefcts vector icons.</remarks>
        Color Stroke
        {
            get
            {
                return _strokeColor;
            }
            set
            {
                if (_strokeColor != value)
                {
                    _strokeColor = value;
                    foreach (C1Icon icon in _templatedIcons)
                    {
                        ((C1VectorIcon)icon).Stroke = value;
                    }
                    foreach (C1Icon icon in _vectorIcons)
                    {
                        ((C1VectorIcon)icon).Stroke = value;
                    }
                }

            }
        }

        /// <summary>
        /// Gets or sets icon stroke thickness.
        /// </summary>
        /// <remarks>This property only afefcts vector icons.</remarks>
        float StrokeThickness
        {
            get
            {
                return _strokeThickness;
            }
            set
            {
                if (_strokeThickness != value)
                {
                    _strokeThickness = value;
                    foreach (C1Icon icon in _templatedIcons)
                    {
                        ((C1VectorIcon)icon).StrokeThickness = _strokeThickness;
                    }
                    foreach (C1Icon icon in _vectorIcons)
                    {
                        ((C1VectorIcon)icon).StrokeThickness = _strokeThickness;
                    }
                }
            }
        }
        #endregion

        #region ** Change Icon Properties
        // change color for all icons
        private void cpColor_SelectedColorChanged(object sender, EventArgs e)
        {
            Color = cpColor.Color;
        }
        // change stroke color for vector icons
        private void cpStroke_SelectedColorChanged(object sender, EventArgs e)
        {
            Stroke = cpStroke.Color;
        }

        // change stroke thickness for vector icons
        private void nbStrokeThickness_ValueChanged(object sender, EventArgs e)
        {
            StrokeThickness = (float)nbStrokeThickness.Value;
        }
        // change antialiasing for vector icons
        private void chkAllowSmoothing_CheckedChanged(object sender, EventArgs e)
        {
            var allowSmoothing = chkAllowSmoothing.Checked;
            foreach (C1Icon icon in _templatedIcons)
            {
                ((C1VectorIcon)icon).AllowSmoothing = allowSmoothing;
            }
            foreach (C1Icon icon in _vectorIcons)
            {
                ((C1VectorIcon)icon).AllowSmoothing = allowSmoothing;
            }
        }
        // allow or disallow recoloring for bitmap icons
        private void chkShowAsMonochrome_CheckedChanged(object sender, EventArgs e)
        {
            ShowAsMonochrome option = chkShowAsMonochrome.Checked ? ShowAsMonochrome.Always : ShowAsMonochrome.Never;
            foreach (C1Icon icon in _bitmapIcons)
            {
                ((C1BitmapIcon)icon).ShowAsMonochrome = option;
            }
        }
        // change font size for font icons
        private void cmbFontSize_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (cmbFontSize.SelectedItem != null)
            {
                var fontSize = int.Parse(cmbFontSize.SelectedItem.Text);
                foreach (C1Icon icon in _fontIcons)
                {
                    ((C1FontIcon)icon).Font = new Font(((C1FontIcon)icon).Font.FontFamily, fontSize);
                }
            }
        }
        // change font face for font icons
        private void cmbFont_SelectedIndexChanged(object sender, EventArgs e)
        {
            // symbols in this sample are only present in Symbolic fonts, so allow to choose between Segoe UI Symbol and Segoe MDL Assets
            if (cmbFonts.SelectedItem != null)
            {
                var fontFamilyName = cmbFonts.SelectedItem.Text;
                foreach (C1Icon icon in _fontIcons)
                {
                    ((C1FontIcon)icon).Font = new Font(fontFamilyName, ((C1FontIcon)icon).Font.Size);
                }
            }
        }
        #endregion

        #region ** Change Main Window Colors
        private void cpForeground_SelectedColorChanged(object sender, EventArgs e)
        {
            this.ForeColor = 
                ugridVectorIcons.ForeColor = 
                ugridFontIcons.ForeColor =
                ugridBitmapIcons.ForeColor =
                ugridTemplatedIcons.ForeColor =
                ugridCompositeIcons.ForeColor = cpForeground.Color;
        }

        private void cpBackground_SelectedColorChanged(object sender, EventArgs e)
        {
            this.BackColor = cpBackground.Color;
        }
        #endregion

        #region ** Switching View
        private void View_Changed(object sender, EventArgs e)
        {
            groupIconSize.Enabled = true;
            btnStretch.Enabled = true;
            if (btnTemplatedIcons.Pressed)
            {
                ugridTemplatedIcons.Visible = true;
                ugridVectorIcons.Visible = false;
                ugridFontIcons.Visible = false;
                ugridBitmapIcons.Visible = false;
                ugridCompositeIcons.Visible = false;
                statusLabel.Text = "Use vector icons defined in the C1IconTemplate class.";
            }
            else if (btnVectorIcons.Pressed)
            {
                ugridTemplatedIcons.Visible = false;
                ugridVectorIcons.Visible = true;
                ugridFontIcons.Visible = false;
                ugridBitmapIcons.Visible = false;
                ugridCompositeIcons.Visible = false;
                statusLabel.Text = "Create vector icons from svg or xaml paths.";
            }
            else if (btnFontIcons.Pressed)
            {
                ugridTemplatedIcons.Visible = false;
                ugridVectorIcons.Visible = false;
                ugridFontIcons.Visible = true;
                ugridBitmapIcons.Visible = false;
                groupIconSize.Enabled = false;
                ugridCompositeIcons.Visible = false;
                statusLabel.Text = "Use Font icons when you need to show clear symbol. Note, Font is not resized automatically.";
            }
            else if (btnBitmapIcons.Pressed) // bitmaps
            {
                ugridTemplatedIcons.Visible = false;
                ugridVectorIcons.Visible = false;
                ugridFontIcons.Visible = false;
                ugridBitmapIcons.Visible = true;
                btnStretch.Enabled = false;
                ugridCompositeIcons.Visible = false;
                statusLabel.Text = "Bitmaps icons can resized or recolored to flat color.";
            }
            else
            {
                ugridTemplatedIcons.Visible = false;
                ugridVectorIcons.Visible = false;
                ugridFontIcons.Visible = false;
                ugridBitmapIcons.Visible = false;
                btnStretch.Enabled = false;
                ugridCompositeIcons.Visible = true;
                statusLabel.Text = "Composite icons can contains different C1Icons.";
            }
        }
        #endregion

        #region ** Change Icon Size
        private void IconSize_Changed(object sender, EventArgs e)
        {
            var size = Size.Empty;
            if (btn16x16.Pressed)
                size = new Size(16, 16);
            else if ( btn32x32.Pressed)
                size = new Size(32, 32);
            else if ( btn48x48.Pressed)
                size = new Size(48, 48);

            foreach (C1Icon icon in _templatedIcons)
            {
                icon.Size = size;
            }
            foreach (C1Icon icon in _vectorIcons)
            {
                icon.Size = size;
            }

            // original bitmaps in this application are 32x32. Scaling them to too big size looks bad, so only allow fixed size.
            if (size == Size.Empty)
            {
                size = new Size(32, 32);
            }
            foreach (C1Icon icon in _bitmapIcons)
            {
                icon.Size = size;
            }

            foreach (C1Icon icon in _compositeIcons)
            {
                icon.Size = size;
            }
        }
        #endregion

    }
